void navigateToSearchScreen(BuildContext context) {
  Navigator.push(
    context,
    MaterialPageRoute(
      builder: (context) => SearchScreen(items: [...vegetables, ...mealPackages]), // Navigasi ke layar pencarian dengan menyediakan daftar item
    ),
  );
}

void navigateToWishlistScreen(BuildContext context) {
  Navigator.push(
    context,
    MaterialPageRoute(
      builder: (context) => WishlistScreen(
        wishlistItems: wishlistItems, // Mengirimkan daftar item ke layar daftar keinginan
        onRemoveItem: (item) {
          // Menentukan tindakan yang akan diambil saat item dihapus dari daftar keinginan
          setState(() {
            wishlistItems.remove(item); // Menghapus item dari daftar keinginan dan memperbarui state
          });
        },
      ),
    ),
  );
}


  Widget categories() {
  List<String> list = ['Sayur', 'Paket Siap Masak']; // List kategori yang akan ditampilkan

  return SizedBox(
    height: 40, // Tinggi dari widget kategori
    child: ListView.builder(
      itemCount: list.length, // Jumlah item dalam list kategori
      scrollDirection: Axis.horizontal, // Arah scroll untuk menampilkan kategori secara horizontal
      itemBuilder: (context, index) {
        return GestureDetector(
          onTap: () {
            setState(() {
              indexCategory = index; // Mengatur index kategori yang dipilih saat di-tap
            });
          },
          child: Container(
            padding: EdgeInsets.fromLTRB(
              index == 0 ? 0 : 16, // Padding kiri untuk item pertama dalam list
              0,
              index == list.length - 1 ? 0 : 16, // Padding kanan untuk item terakhir dalam list
              0,
            ),
            alignment: Alignment.center, // Posisi teks dalam container
            child: Text(
              list[index], // Teks kategori yang ditampilkan
              style: TextStyle(
                fontSize: 22, // Ukuran font teks
                color: indexCategory == index ? Colors.green : Colors.grey, // Warna teks tergantung pada apakah kategori dipilih atau tidak
                fontWeight: indexCategory == index ? FontWeight.bold : null, // Ketebalan teks jika kategori dipilih
              ),
            ),
          ),
        );
      },
    ),
  );
}


  Widget gridItems(List<dynamic> items) {
  return GridView.builder(
    itemCount: items.length, // Jumlah total item yang akan ditampilkan dalam GridView
    shrinkWrap: true, // Membungkus GridView agar sesuai dengan ukurannya
    physics: NeverScrollableScrollPhysics(), // Mengatur agar GridView tidak dapat di-scroll
    gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
      crossAxisCount: 2, // Jumlah kolom dalam grid
      mainAxisSpacing: 8, // Spasi antar item dalam satu baris (sumbu utama)
      crossAxisSpacing: 8, // Spasi antar kolom (sumbu lintang)
      mainAxisExtent: 320, // Tinggi dari setiap item dalam grid (sumbu utama)
    ),
    itemBuilder: (context, index) {
      var item = items[index]; // Mengambil item pada indeks tertentu dari daftar items
      if (item is Vegetable) {
        return buildVegetableItem(item); // Membangun item sayur jika item adalah instance dari Vegetable
      } else if (item is MealPackage) {
        return buildMealPackageItem(item); // Membangun item paket siap masak jika item adalah instance dari MealPackage
      }
      return Container(); // Fallback jika tipe item tidak dikenali, mengembalikan kontainer kosong
    },
  );
}


 Widget buildVegetableItem(Vegetable item) {
  return GestureDetector(
    onTap: () {
      Navigator.push(
        context,
        MaterialPageRoute(
          builder: (context) => DetailPage(item: item), // Navigasi ke halaman detail dengan mengirimkan objek Vegetable
        ),
      );
    },
    child: Container(
      decoration: BoxDecoration(
        color: Colors.grey[200], // Warna latar belakang kontainer
        borderRadius: BorderRadius.circular(16), // Membuat sudut kontainer menjadi bulat
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          SizedBox(height: 16), // Jarak kosong di atas gambar
          Center(
            child: ClipRRect(
              borderRadius: BorderRadius.circular(120), // Membuat sudut gambar menjadi bulat
              child: Image.network(
                item.imageUrl, // URL gambar dari objek Vegetable
                fit: BoxFit.cover, // Mengatur gambar agar memenuhi kontainer
                width: 120,
                height: 120,
                errorBuilder: (context, error, stackTrace) {
                  return Icon(Icons.error, color: Colors.red); // Tampilan ikon kesalahan jika gagal memuat gambar
                },
                loadingBuilder: (context, child, loadingProgress) {
                  if (loadingProgress == null) return child;
                  return Center(
                    child: CircularProgressIndicator(
                      value: loadingProgress.expectedTotalBytes != null
                          ? loadingProgress.cumulativeBytesLoaded / (loadingProgress.expectedTotalBytes ?? 1)
                          : null,
                    ), // Tampilan indikator loading saat gambar sedang dimuat
                  );
                },
              ),
            ),
          ),
          SizedBox(height: 16), // Jarak kosong setelah gambar
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 8.0),
            child: Text(
              item.name, // Nama sayur dari objek Vegetable
              style: TextStyle(
                color: Colors.black,
                fontWeight: FontWeight.bold,
                fontSize: 18,
              ),
            ),
          ),
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 8.0),
            child: Text(
              'Rp ${item.price}', // Harga sayur dari objek Vegetable
              style: TextStyle(
                color: Colors.grey,
                fontSize: 16,
              ),
            ),
          ),
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 8.0),
            child: Row(
              children: [
                Icon(Icons.star, color: Colors.yellow[700], size: 16), // Icon bintang
                SizedBox(width: 4),
                Text(
                  item.rating.toString(), // Rating sayur dari objek Vegetable
                  style: TextStyle(
                    color: Colors.grey,
                    fontSize: 14,
                  ),
                ),
              ],
            ),
          ),
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 8.0),
            child: Row(
              mainAxisAlignment: MainAxisAlignment.spaceBetween,
              children: [
                IconButton(
                  icon: Icon(Icons.favorite_border, color: wishlistItems.contains(item) ? Colors.red : Colors.grey), // Tombol favorit
                  onPressed: () {
                    setState(() {
                      if (wishlistItems.contains(item)) {
                        wishlistItems.remove(item); // Menghapus item dari wishlist jika sudah ditambahkan sebelumnya
                      } else {
                        wishlistItems.add(item); // Menambahkan item ke wishlist jika belum ditambahkan sebelumnya
                      }
                    });
                  },
                ),
                Row(
                  children: [
                    IconButton(
                      icon: Icon(Icons.remove, color: Colors.red), // Tombol kurang
                      onPressed: () {
                        setState(() {
                          if (itemCounts[item.id] != null && itemCounts[item.id]! > 0) {
                            itemCounts[item.id] = itemCounts[item.id]! - 1; // Mengurangi jumlah item jika jumlahnya lebih dari 0
                          }
                        });
                      },
                    ),
                    IconButton(
                      icon: Icon(Icons.add, color: Colors.green), // Tombol tambah
                      onPressed: () {
                        setState(() {
                          itemCounts[item.id] = (itemCounts[item.id] ?? 0) + 1; // Menambah jumlah item dalam keranjang
                        });
                      },
                    ),
                    Text(
                      (itemCounts[item.id] ?? 0).toString(), // Menampilkan jumlah item yang telah ditambahkan
                      style: TextStyle(
                        fontSize: 16,
                        fontWeight: FontWeight.bold,
                        color: Colors.black,
                      ),
                    ),
                  ],
                ),
              ],
            ),
          ),
        ],
      ),
    ),
  );
}

